export function formatMaxImagePreview(value) {
  return `max-image-preview:${value}`;
}
export function formatMaxSnippet(value) {
  return `max-snippet:${value}`;
}
export function formatMaxVideoPreview(value) {
  return `max-video-preview:${value}`;
}
